public class WalkingDeadS2ReviewGenerator {
      public Review generate() {
          Review r = getReviews().getWalkingDeadS1Review().clone();
          r.setAwesomeProtagonist(true);
          //r.setMeaningfulChoices(true);
          r.setMeaningfulChoices(false);
          r.setFavoriteCharacterDeathProbability(0.95);
          r.getFood().clear();
          r.getWildlife().add(new DeliciousRaccoons());
          r.setStoryMode(StoryMode.IMMERSIVE);
          r.setOverallRating("Even better than you think");
          return r;
     }
}
